# Functions to access the above tables

def find_mass_loading(mass,redshift):
    if redshift<=0.2 or redshift>=4.:
        zid = (np.abs(Nelson19[1]-redshift)).argmin()
        mid = (np.abs(Nelson19[0][zid][:,0]-np.log10(mass))).argmin()
        return Nelson19[0][zid][mid,1],Nelson19[0][zid][mid,2],Nelson19[0][zid][mid,3]
    else:
        zidprev = bisect.bisect(Nelson19[1],redshift) - 1
        midnext = (np.abs(Nelson19[0][zidprev+1][:,0]-np.log10(mass))).argmin()
        midprev = (np.abs(Nelson19[0][zidprev][:,0]-np.log10(mass))).argmin()
        fracnext = (redshift - Nelson19[1][zidprev]) / (Nelson19[1][zidprev+1] - Nelson19[1][zidprev])
        fracprev = 1.-fracnext
        return fracprev*Nelson19[0][zidprev][midprev,1]+fracnext*Nelson19[0][zidprev+1][midnext,1],fracprev*Nelson19[0][zidprev][midprev,2]+fracnext*Nelson19[0][zidprev+1][midnext,2],fracprev*Nelson19[0][zidprev][midprev,3]+fracnext*Nelson19[0][zidprev+1][midnext,3]

def find_reaccretion_time(mass,redshift):
    zid = (np.abs(TNG100_zM[0]-redshift)).argmin()
    mid = (np.abs(TNG100_zM[1]-np.log10(mass))).argmin()
    return TNG100_reaccretiontime_vel0[zid,mid],TNG100_reaccretiontime_vel150[zid,mid],TNG100_reaccretiontime_vel300[zid,mid]

def find_yield(mass,metallicity,SNyields,AGByields,nisotopes,isotopes):
    if mass>=9:
        # Use SNyields
        mass_yields, mets, elements = yield_dict[SNyields]
        if isotopes!=elements:
            print("SN elements do not match up with lookup dictionary")
            stop
        metidx = (np.abs(np.log10(mets/metallicity))).argmin()
        idx = (np.abs(mass_yields[:,0]-mass)).argmin()
    else:
        # use AGByields
        mass_yields, mets, elements = yield_dict[AGByields]
        if isotopes!=elements:
            print("AGB elements do not match up with lookup dictionary")
            stop
        metidx = (np.abs(np.log10(mets/metallicity))).argmin()
        idx = (np.abs(mass_yields[:,0]-mass)).argmin()
    return [mass_yields[idx][1+metidx+iso*len(mets)] for iso in range(nisotopes)]

